import static android.app.Activity.RESULT_OK;
import static android.content.ContentValues.TAG;


/**
 * Created by arc on 31/12/16.
 */

public class ProductNewFragment extends Fragment {

    private static final int RESULT_LOAD_IMAGE = 1;
    private static final int PICK_IMAGE_REQUEST = 1;
    View productNewFragmentView;
    byte[] byteArray;
    String decoded;
    int categoryid;
    EditText productTitle;
    EditText price;
    EditText description;
    Button upload;
    Button btn_image;

    Spinner spinner;
    AdapterCategories adapterSpinner;



    @Nullable
    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container, Bundle savedInstanceState) {
        productNewFragmentView = inflater.inflate(R.layout.productnew_fragment,container,false);
        spinner = (Spinner) productNewFragmentView.findViewById(R.id.spinner_pcategories);
        adapterSpinner = new AdapterCategories(getActivity(), APIHelper.getGlobalCatList());
        adapterSpinner.notifyDataSetChanged();
        spinner.setAdapter(adapterSpinner);


        //INIT

        productTitle = (EditText) productNewFragmentView.findViewById(R.id.et_productTitle);
        price = (EditText) productNewFragmentView.findViewById(R.id.et_productPrice);
        description = (EditText) productNewFragmentView.findViewById(R.id.et_productDescription);
        upload = (Button) productNewFragmentView.findViewById(R.id.btn_upload);
        btn_image = (Button) productNewFragmentView.findViewById(R.id.btn_image);
        btn_image.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                Intent i = new Intent(Intent.ACTION_PICK,
                        android.provider.MediaStore.Images.Media.EXTERNAL_CONTENT_URI);
                startActivityForResult(i, RESULT_LOAD_IMAGE);
            }
        });
        upload.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                 categoryid= spinner.getSelectedItemPosition();

               if(validateInput())
               {
                   JsonObject jo = new JsonObject();
                   jo.addProperty("Title", productTitle.getText().toString());
                   jo.addProperty("Description", description.getText().toString());
                   jo.addProperty("Price", price.getText().toString());
                   jo.addProperty("CategoryId",String.valueOf(categoryid));
                   jo.addProperty("OwnerId",String.valueOf(APIHelper.getApiHelper().getLoggedUserId()));


                   jo.addProperty("Picture",decoded);


                   Log.d(TAG, "onClickPicture: "+ new Gson().toJson(jo));
                    API.getServices.getAppApiServices().postProduct(jo).enqueue(new Callback<ResponseBody>() {
                        @Override
                        public void onResponse(Call<ResponseBody> call, Response<ResponseBody> response) {
                            Toast.makeText(getActivity(), "Done", Toast.LENGTH_SHORT).show();
                            getFragmentManager().beginTransaction().replace(R.id.flyout, new HomePageFragment()).commit();
                        }

                        @Override
                        public void onFailure(Call<ResponseBody> call, Throwable t) {

                        }
                    });
               }
            }
        });


        return productNewFragmentView;
    }

    private boolean validateInput() {
        if(productTitle.getText().toString().matches(""))
        {
            Toast.makeText(getActivity(), "Please provide product title", Toast.LENGTH_SHORT).show();
            return false;
        }
        else if (price.getText().toString().matches(""))
        {
            Toast.makeText(getActivity(), "Please provide product price", Toast.LENGTH_SHORT).show();
            return false;

        }
        else if (description.getText().toString().matches(""))
        {
            Toast.makeText(getActivity(), "Please provide product desc", Toast.LENGTH_SHORT).show();
            return false;

        }
        else if(decoded == null)
        {
            Toast.makeText(getActivity(), "Please provide product image", Toast.LENGTH_SHORT).show();
            return false;
        }
        else if(categoryid < 1)
        {
            Toast.makeText(getActivity(), "Please select category", Toast.LENGTH_SHORT).show();

            return false;
        }
        return true;
    }

    @Override
    public void onActivityResult(int requestCode, int resultCode, Intent data) {
        super.onActivityResult(requestCode, resultCode, data);

        if (requestCode == PICK_IMAGE_REQUEST && resultCode == RESULT_OK && data != null && data.getData() != null) {

            Uri uri = data.getData();

            try {
                Bitmap bitmap = MediaStore.Images.Media.getBitmap(getActivity().getContentResolver(), uri);
                // Log.d(TAG, String.valueOf(bitmap));

                ImageView imageView = (ImageView) productNewFragmentView.findViewById(R.id.product_image);
                imageView.setImageBitmap(bitmap);


                ByteArrayOutputStream baos = new ByteArrayOutputStream();

                bitmap.compress(Bitmap.CompressFormat.JPEG, 100, baos);

                byte[] byteArrayImage = baos.toByteArray();

                decoded = Base64.encodeToString(byteArrayImage, Base64.DEFAULT);


                Toast.makeText(getActivity(), "Array" + byteArray, Toast.LENGTH_SHORT).show();

            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }
    public String backPic() {
        return "";
    }
}
